mapper.select:
SELECT IFNULL(SUM(t.price), 0) AS price, t.owner_user_id FROM crm_contract t WHERE t.deleted = 0 AND t.audit_status = 20 and t.owner_user_id in
#{item}
AND t.order_date between #{startTime} and #{endTime} GROUP BY t.owner_user_id ORDER BY price DESC

SELECT IFNULL(SUM(t.price), 0) AS price, t.owner_user_id FROM crm_receivable t WHERE t.deleted = 0 AND t.audit_status = 20 and t.owner_user_id in
#{item}
AND t.return_time between #{startTime} and #{endTime} GROUP BY t.owner_user_id ORDER BY price DESC
