mapper.select:
SELECT IFNULL(SUM(price), 0) AS count, owner_user_id FROM crm_contract WHERE deleted = 0 AND audit_status = 20 and owner_user_id in
#{userId}
AND order_date between #{times[0],javaType=java.time.LocalDateTime} and #{times[1],javaType=java.time.LocalDateTime} GROUP BY owner_user_id

SELECT IFNULL(SUM(price), 0) AS count, owner_user_id FROM crm_receivable WHERE deleted = 0 AND audit_status = 20 and owner_user_id in
#{userId}
AND return_time between #{times[0],javaType=java.time.LocalDateTime} and #{times[1],javaType=java.time.LocalDateTime} GROUP BY owner_user_id
