SELECT DATE_FORMAT( create_time, #{sqlDateFormat,javaType=java.lang.String} ) AS category, count(*) AS count FROM crm_customer WHERE deleted = 0 AND owner_user_id IN
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY category

SELECT DATE_FORMAT( b.order_date, #{sqlDateFormat,javaType=java.lang.String} ) AS category, count( DISTINCT a.id ) AS count FROM crm_customer AS a LEFT JOIN crm_contract AS b ON b.customer_id = a.id WHERE a.deleted = 0 AND b.deleted = 0 AND b.audit_status = 20 AND a.owner_user_id IN
#{userId}
AND b.create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY category

SELECT DATE_FORMAT( create_time, #{sqlDateFormat,javaType=java.lang.String} ) AS category, count(*) AS count FROM crm_follow_up_record WHERE creator IN
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} AND biz_type = #{bizType,javaType=java.lang.Integer} GROUP BY category

SELECT DATE_FORMAT( create_time, #{sqlDateFormat,javaType=java.lang.String} ) AS category, count(DISTINCT biz_id) AS count FROM crm_follow_up_record WHERE creator IN
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} AND biz_type = #{bizType,javaType=java.lang.Integer} GROUP BY category

SELECT type AS category, count(*) AS count FROM crm_follow_up_record WHERE creator IN
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} AND biz_type = #{bizType,javaType=java.lang.Integer} GROUP BY category

SELECT DATE_FORMAT( b.order_date, #{sqlDateFormat,javaType=java.lang.String} ) AS category, IFNULL( TRUNCATE ( AVG( TIMESTAMPDIFF( DAY, a.create_time, b.order_date )), 1 ), 0 ) AS cycle FROM crm_customer AS a LEFT JOIN crm_contract AS b ON b.customer_id = a.id WHERE a.deleted = 0 AND b.deleted = 0 AND b.audit_status = 20 AND a.owner_user_id IN
#{userId}
AND b.create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY category
