SELECT DATE_FORMAT( create_time, #{sqlDateFormat} ) AS time, COUNT(*) AS customerCreateCount FROM crm_customer WHERE deleted = 0 AND owner_user_id IN
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY time

SELECT DATE_FORMAT( order_date, #{sqlDateFormat} ) AS time, COUNT( DISTINCT customer_id ) AS customerDealCount FROM crm_contract WHERE deleted = 0 AND audit_status = 20 AND owner_user_id IN
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY time

SELECT owner_user_id, COUNT(1) AS customer_create_count FROM crm_customer WHERE deleted = 0 AND owner_user_id in
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY owner_user_id

SELECT customer.owner_user_id, COUNT( DISTINCT customer.id ) AS customer_deal_count FROM crm_customer AS customer LEFT JOIN crm_contract AS contract ON contract.customer_id = customer.id WHERE customer.deleted = 0 AND contract.deleted = 0 AND contract.audit_status = 20 AND customer.owner_user_id IN
#{userId}
AND contract.create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY customer.owner_user_id

SELECT owner_user_id, IFNULL(SUM(total_price), 0) AS contract_price FROM crm_contract WHERE deleted = 0 AND audit_status = 20 AND owner_user_id in
#{userId}
AND order_date BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY owner_user_id

SELECT owner_user_id, IFNULL(SUM(price), 0) AS receivable_price FROM crm_receivable WHERE deleted = 0 AND audit_status = 20 AND owner_user_id IN
#{userId}
AND return_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY owner_user_id

SELECT
    DATE_FORMAT( create_time, #{sqlDateFormat} ) AS time,
    COUNT(*) AS followup_record_count
  FROM crm_follow_up_record
 WHERE creator IN

            #{userId}

   AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime}
   AND biz_type = ${@cn.iocoder.yudao.module.crm.enums.common.CrmBizTypeEnum@CRM_CUSTOMER.type}
 GROUP BY time

SELECT
    DATE_FORMAT( create_time, #{sqlDateFormat} ) AS time,
    COUNT(DISTINCT biz_id) AS followup_customer_count
  FROM crm_follow_up_record
 WHERE creator IN

            #{userId}

   AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime}
   AND biz_type = ${@cn.iocoder.yudao.module.crm.enums.common.CrmBizTypeEnum@CRM_CUSTOMER.type}
 GROUP BY time

SELECT
    creator as owner_user_id,
    COUNT(*) AS followup_record_count
  FROM crm_follow_up_record
 WHERE creator IN

            #{userId}

   AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime}
   AND biz_type = ${@cn.iocoder.yudao.module.crm.enums.common.CrmBizTypeEnum@CRM_CUSTOMER.type}
 GROUP BY creator

SELECT
    creator as owner_user_id,
    COUNT(DISTINCT biz_id) AS followup_customer_count
  FROM crm_follow_up_record
 WHERE creator IN

            #{userId}

   AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime}
   AND biz_type = ${@cn.iocoder.yudao.module.crm.enums.common.CrmBizTypeEnum@CRM_CUSTOMER.type}
 GROUP BY creator

SELECT
    type AS followupType,
    COUNT(*) AS followup_record_count
  FROM crm_follow_up_record
 WHERE creator IN

            #{userId}

   AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime}
   AND biz_type = ${@cn.iocoder.yudao.module.crm.enums.common.CrmBizTypeEnum@CRM_CUSTOMER.type}
 GROUP BY followupType

SELECT customer.`name` AS customer_name, contract.`name` AS contract_name, contract.total_price, IFNULL( receivable.price, 0 ) AS receivable_price, customer.industry_id, customer.source, customer.owner_user_id, customer.creator AS creator_user_id, customer.create_time, contract.order_date FROM crm_customer AS customer INNER JOIN crm_contract AS contract ON customer.id = contract.customer_id LEFT JOIN crm_receivable AS receivable ON contract.id = receivable.contract_id WHERE customer.deleted = 0 AND contract.deleted = 0 AND receivable.deleted = 0 AND contract.audit_status = 20 AND customer.owner_user_id IN
#{userId}
AND contract.create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime}

SELECT DATE_FORMAT( contract.order_date, #{sqlDateFormat} ) AS time, IFNULL( TRUNCATE ( AVG( TIMESTAMPDIFF( DAY, customer.create_time, contract.order_date )), 1 ), 0 ) AS customer_deal_cycle FROM crm_customer AS customer LEFT JOIN crm_contract AS contract ON contract.customer_id = customer.id WHERE customer.deleted = 0 AND contract.deleted = 0 AND contract.audit_status = 20 AND customer.owner_user_id IN
#{userId}
AND contract.create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY time

SELECT customer.owner_user_id, IFNULL( TRUNCATE ( AVG( TIMESTAMPDIFF( DAY, customer.create_time, contract.order_date )), 1 ), 0 ) AS customer_deal_cycle FROM crm_customer AS customer LEFT JOIN crm_contract AS contract ON contract.customer_id = customer.id WHERE customer.deleted = 0 AND contract.deleted = 0 AND contract.audit_status = 20 AND customer.owner_user_id IN
#{userId}
AND contract.create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY customer.owner_user_id
