mapper.select:
SELECT industry_id, COUNT(*) AS customerCount, SUM(deal_status) AS dealCount, ROUND(COUNT(*) / (SELECT COUNT(*) FROM crm_customer WHERE deleted = 0) * 100, 2) AS industryPortion, ROUND(SUM(deal_status) / NULLIF(COUNT(*), 0) * 100, 2) AS dealPortion FROM crm_customer WHERE deleted = 0 AND industry_id IS NOT NULL AND owner_user_id IN
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY industry_id;

SELECT source, COUNT(*) AS customerCount, SUM(deal_status) AS dealCount, ROUND(COUNT(*) / (SELECT COUNT(*) FROM crm_customer WHERE deleted = 0) * 100, 2) AS sourcePortion, ROUND(SUM(deal_status) / NULLIF(COUNT(*), 0) * 100, 2) AS dealPortion FROM crm_customer WHERE deleted = 0 AND source IS NOT NULL AND owner_user_id IN
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY source;

SELECT level, COUNT(*) AS customerCount, SUM(deal_status) AS dealCount, ROUND(COUNT(*) / (SELECT COUNT(*) FROM crm_customer WHERE deleted = 0) * 100, 2) AS levelPortion, ROUND(SUM(deal_status) / NULLIF(COUNT(*), 0) * 100, 2) AS dealPortion FROM crm_customer WHERE deleted = 0 AND level IS NOT NULL AND owner_user_id IN
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY level;

SELECT area_id, COUNT(*) AS customerCount, SUM(deal_status) AS dealCount, ROUND(COUNT(*) / (SELECT COUNT(*) FROM crm_customer WHERE deleted = 0) * 100, 2) AS areaPortion, ROUND(SUM(deal_status) / NULLIF(COUNT(*), 0) * 100, 2) AS dealPortion FROM crm_customer WHERE deleted = 0 AND area_id IS NOT NULL AND owner_user_id IN
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY area_id;
