mapper.select:
SELECT bu.id, bu.bind_user_time AS brokerageTime, u.nickname, u.avatar, (SELECT SUM(price) FROM trade_brokerage_record r WHERE r.user_id = u.id AND biz_type = 1 AND r.status = 1 AND r.deleted = FALSE) AS brokeragePrice, (SELECT COUNT(1) FROM trade_brokerage_record r WHERE r.user_id = u.id AND biz_type = 1 AND r.status = 1 AND r.deleted = FALSE) AS brokerageOrderCount, (SELECT COUNT(1) FROM trade_brokerage_user c WHERE c.bind_user_id = u.id AND c.deleted = FALSE) AS brokerageUserCount FROM member_user AS u JOIN trade_brokerage_user AS bu ON bu.id = u.id
AND u.nickname LIKE concat('', #{param.nickname}, '')
AND bu.bind_user_id = #{userId}
AND bu.bind_user_id = (SELECT id FROM trade_brokerage_user c WHERE c.bind_user_id = #{userId})
ORDER BY brokerageUserCount ${param.sortingField.order}
ORDER BY brokerageOrderCount ${param.sortingField.order}
ORDER BY brokeragePrice ${param.sortingField.order}
ORDER BY bu.bind_user_time DESC
